import React, { Component, useEffect, useState } from 'react'

const Github = () => {

    const [user, setUser] = useState([{}])
    const [repos, setRepos] = useState([{}])

    useEffect(() => {
        console.log('Fetching data from backend...')
        fetch('/github/user')
            .then(response => response.json())
            .then(data => {
                setUser(data)
            })
        fetch('/github/repos')
            .then(response => response.json())
            .then(data => {
                setRepos(data)
            })
    }, [])

    return (
        <div className='mt-2'>
            {(typeof user.id != 'undefined') ? (
                <div>
                    <div className='flex-row flex max-h-20'>
                        <a href={user.html_url} target="_blank" rel="noopener noreferrer">
                            <img src={user.avatar_url} alt='Github Avatar' className="min-w-20 h-20" />
                        </a>
                        <div className='ml-3'>
                            <p className='stat-h1 leading-5 mt-1'>Public Repos: {user.public_repos}</p>
                            {repos[0] != null ? <p className='stat-h2 text-xs opacity-70 mt-1'>{repos[0].name}</p> : null}
                            {repos[1] != null ? <p className='stat-h2 text-xs opacity-70 mt-1'>{repos[1].name}</p> : null}
                        </div>
                    </div>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default Github
